import { useEffect, useState } from 'react';
export function createFetchHook(promise, refresh = 0) {
  const [state, setState] = useState();
  useEffect(() => {
    setState(undefined);

    async function fetchData() {
      const data = await promise();
      setState(data);
    }

    fetchData();
  }, [refresh]);
  return state;
}
