import { backendAPI } from '../../api';

class RankService {
  async create(rankDTO) {
    const newRank = await backendAPI.post('ranks', rankDTO);
    return newRank.data;
  }

  async getAll() {
    const ranks = await backendAPI.get('ranks');
    return ranks.data;
  }

  async getStaff() {
    const ranks = await backendAPI.get('ranks/staff');
    return ranks.data;
  }

  async getByID(rankID) {
    const rank = await backendAPI.get(`ranks/${rankID}`);
    return rank.data;
  }

  async updateByID(rankID, rankDTO) {
    await backendAPI.patch(`ranks/${rankID}`, rankDTO);
  }

  async deleteByID(rankID) {
    await backendAPI.delete(`ranks/${rankID}`);
  }

}

export const rankService = new RankService();
