import { backendAPI } from '../../api';
export class GuestbookServiceImplementation {
  async getAllForUser(userID) {
    const posts = await backendAPI.get(`users/${userID}/guestbook`);
    return posts.data;
  }

  async create(userID, guestbookDTO) {
    const newPost = await backendAPI.post(`users/${userID}/guestbook`, guestbookDTO);
    return newPost.data;
  }

  async update(userID, postID, guestbookDTO) {
    const updatedPost = await backendAPI.patch(`users/${userID}/guestbook/${postID}`);
    return updatedPost.data;
  }

  async delete(userID, postID) {
    await backendAPI.delete(`users/${userID}/guestbook/${postID}`);
  }

}
