import { backendAPI } from '../../api';

class BanServiceImplementation {
  async create(banDTO) {
    const newBan = await backendAPI.post('user/bans', banDTO);
    return newBan.data;
  }

  async getAll() {
    const bans = await backendAPI.get('user/bans');
    return bans.data;
  }

  async getByID(banID) {
    const ban = await backendAPI.get(`user/bans/${banID}`);
    return ban.data;
  }

  async updateByID(banID, banDTO) {
    await backendAPI.patch(`user/bans/${banID}`, banDTO);
  }

  async deleteByID(banID) {
    await backendAPI.delete(`user/bans/${banID}`);
  }

}

export const banService = new BanServiceImplementation();
