import { backendAPI } from '../../api';

class GroupServiceImplementation {
  async getAll() {
    const groups = await backendAPI.get('groups');
    return groups.data;
  }

  async getMostPopular() {
    const groups = await backendAPI.get('groups');
    return groups.data;
  }

  async getByID(groupID) {
    const group = await backendAPI.get(`groups/${groupID}`);
    return group.data;
  }

}

export const groupService = new GroupServiceImplementation();
